CREATE TABLE UTENTE (id_utente INTEGER NOT NULL, COGNOME VARCHAR(255), DATADINASCITA DATE, EMAIL VARCHAR(255), immagine VARCHAR(255), INDIRIZZO VARCHAR(255), NOME VARCHAR(255), NOMESOCIETA VARCHAR(255), p_iva VARCHAR(255), PASSWORD VARCHAR(255), RUOLO INTEGER, PRIMARY KEY (id_utente))
CREATE TABLE PRENOTAZIONE (id_prenotazione INTEGER NOT NULL, POSTI_PRENOTATI INTEGER, PROIEZIONE INTEGER, UTENTE INTEGER, PRIMARY KEY (id_prenotazione))
CREATE TABLE PROIEZIONE (id_proiezione INTEGER AUTO_INCREMENT NOT NULL, DATAORA DATETIME, INTERVALLO INTEGER, POSTIMAX INTEGER, id_film INTEGER, PRIMARY KEY (id_proiezione))
CREATE TABLE FILM (id_film INTEGER NOT NULL, anno_di_uscita VARCHAR(255), durata INTEGER, genere VARCHAR(255), locandina VARCHAR(255), titolo VARCHAR(255), trama VARCHAR(255), url_trailer VARCHAR(255), PRIMARY KEY (id_film))
ALTER TABLE PROIEZIONE ADD CONSTRAINT FK_PROIEZIONE_id_film FOREIGN KEY (id_film) REFERENCES FILM (id_film)
